args.value:
.job-status-badge {
  padding: 3px 8px;
  border-radius: 3px;
  font-size: 12px;
  font-weight: bold;
}
.status-queued { background-color: #f0ad4e; color: white; }
.status-running { background-color: #5bc0de; color: white; }
.status-completed { background-color: #5cb85c; color: white; }
.status-failed { background-color: #d9534f; color: white; }

.job-monitor-card {
  border: 1px solid #ddd;
  border-radius: 5px;
  padding: 15px;
  margin-bottom: 15px;
  background: white;
}

.metric-box {
  text-align: center;
  padding: 10px;
  border: 1px solid #e0e0e0;
  border-radius: 3px;
  background: #f9f9f9;
}

.metric-value {
  font-size: 24px;
  font-weight: bold;
  color: #333;
}

.metric-label {
  font-size: 12px;
  color: #666;
  text-transform: uppercase;
}
